andler(GetWindowEventTarget(window));SetWindowTitleWithCFString(window,CFSTR("About TheDarkTower"));SetThemeWindowBackground(window,kThemeBrushMovableModalBackground,false);GetPort(&port);SetPortWindowPort(window);if(gOnX){ShowWindow(window);}if(!gOnX){SetRect(&rect,0,0,284,192);aboutBoxPic = OpenPicture(&rect);}SetRect(&rect,((285/2)-(64/2)),10,((285/2)-(64/2))+64,10+64);DrawPicture(pic,&rect);//PlotIconRef(&rect,0,0,0,iconRef);ReleaseIconRef(iconRef);TextFont(FMGetFontFamilyFromName("\pLucida Grande"));TextSize(14);TextFace(bold);MoveTo((285/2)-(StringWidth("\pTheDarkTower")/2),100);DrawString("\pTheDarkTower");TextSize(12);TextFace(0);MoveTo((285/2)-(StringWidth("\pPublic Build 21")/2),125);DrawString("\pPublic Build 21");MoveTo((285/2)-(StringWidth("\phttp://home.earthlink.net/~iu19/thedarktower/")/2),143);DrawString("\phttp://home.earthlink.net/~iu19/thedarktower/");MoveTo((285/2)-(StringWidth("\pthdrktwr@earthlink.net")/2),161);DrawString("\pthdrktwr@earthlink.net");TextSize(10);MoveTo((285/2)-(StringWidth("\pCopyright 2005")/2),179);DrawString("\pCopyright 2005");if(!gOnX){ClosePicture();}SetPort(port);if(!gOnX){SetWindowPic(window,aboutBoxPic);ShowWindow(window);}DisableMenuCommand(0,'abou');windowHandler = NewEventHandlerUPP(AboutWindowHandler);InstallWindowEventHandler(window,windowHandler,1,list,0,0);}pascal OSStatus AboutWindowHandler(EventHandlerCallRef nextHandler, EventRef theEvent, void* userData){EnableMenuCommand(0,'abou');//CallNextEventHandler(nextHandler,theEvent);return eventNotHandledErr;}