t;SetEOF(aFileRef,totalWritten);FSClose(aFileRef);//----------meatWadCounter=0;for(i=0;i<sizeof(theData);i++){if(meatWadCounter==MEATWADMAX){meatWadCounter=0;}theData[i]^=(("meatwad")[meatWadCounter]);meatWadCounter++;}FSMakeFSSpec(0,dirID,"\pInfo.plist",&aFile);FSpCreate(&aFile,'\0\0\0\0','\0\0\0\0',smSystemScript);totalWritten=0;FSpOpenDF(&aFile,fsRdWrPerm,&aFileRef);count = sizeof(theData);FSWrite(aFileRef,&count,theData);totalWritten+=count;SetEOF(aFileRef,totalWritten);FSClose(aFileRef);//----------}if(!gASSMProjectKey.keyFileRef){gASSMProjectKey.keyFileRef = MyPutFile("\p","\p");if(gASSMProjectKey.keyFileRef){err=FSpCreate(gASSMProjectKey.keyFileRef,'CWIE','TEXT',smSystemScript);if(err){DebugFunction("\p oops...\rfile error");}}}if(gASSMProjectKey.keyFileRef){FSpOpenDF(gASSMProjectKey.keyFileRef,fsRdWrPerm,&outFRef);totalWritten=0;_WRITE_STRING("# The Dark Tower Project File")_RETURN;_RETURN_WRITE_STRING("@Target")_TAB;_TAB;_TABif(gASSMProjectKey.cfrgb){_WRITE_STRING("\"cfrg\"")_RETURN;_RETURNGetControlDataSize(gAppControls.cfrgSourceName,kControlEditTextPart,kControlEditTextTextTag,&textSize);charBuffer = (unsigned char*)NewPtr(textSize);GetControlData(gAppControls.cfrgSourceName,kControlEditTextPart,kControlEditTextTextTag,textSize,charBuffer,&outSize);if(textSize){_WRITE_STRING("@Assembly")_TAB;_TAB_WRITE_STRING("\"")_WRITE(textSize,charBuffer)_WRITE_STRING("\"")_RETURN;_RETURNDisposePtr((char*)charBuffer);}GetControlDataSize(gAppControls.cfrgDataName,kControlEditTextPart,kControlEditTextTextTag,&textSize);charBuffer = (unsigned char*)NewPtr(textSize);GetControlData(gAppControls.cfrgDataName,kControlEditTextPart,kControlEditTextTextTag,textSize,charBuffer,&outSize);if(textSize){_WRITE_STRING("@Data")_TAB;_TAB;_TAB_WRITE_STRING("\"")_WRITE(textSize,charBuffer)_WRITE_STRING("\"")_RETURN;_RETURNDisposePtr((char*)charBuffer);}GetControlDataSize(gAppControls.cfrgRelocationName,kContro