//NavCustomControl(navDialog,kNavCtlSetLocation,&defaultLocation);NavDialogRun(navDialog);navAction = NavDialogGetUserAction(navDialog);if(navAction==kNavUserActionChoose){NavDialogGetReply(navDialog,&navReply);AEGetNthPtr(&(navReply.selection), 1, typeFSS,&theKeyword, &actualType, selectedFSS, sizeof(FSSpec),&actualSize );}else{DisposePtr((char*)selectedFSS);selectedFSS = 0;}NavDialogDispose(navDialog);return selectedFSS;}