Record	= &fakeAsmRecord;				fakeAsmRecord.instcount = 0;				fakeAsmRecord.insts = (long*)NewPtr(macroSize*sizeof(long)); //look here!				for(k=0;k<macroSize;k++)				{				Byte_DEFAULT(&macroAsmPB);				(*macroAsmPB.bMarker)++;				}				(*macroAsmPB.bMarker)++;				i = (*macroAsmPB.bMarker);				(*currAsmMacros)->data = fakeAsmRecord.insts;				(*currAsmMacros)->count = fakeAsmRecord.instcount;				currAsmMacros = &((*currAsmMacros)->next);				}			break;			}	}}return asmMacros;}